import React from "react";
import CurrentTime from "./CurrentTime";
import WeatherIcon from "./WeatherIcon";
import WeatherTemperature from "./WeatherTemperature";
import "./WeatherApp.css";
import axios from "axios";

export default function WeatherInfo(props) {
  return (
    <div className="WeatherInformation">
      <div className="col-sm-5 justify-content-left">
        <p className="lc">
          {props.data.city}
          {/* Vienna */}
        </p>
      </div>
      <div className="row d-flex justify-content-between">
        <div className="col-sm-7">
          <WeatherIcon code={props.data.icon} size={52} className="icon" />
          {/* <img
                    src="https://em-content.zobj.net/thumbs/120/google/350/sun-behind-cloud_26c5.png"
                    alt=""
                    className="icon"
                  /> */}
          <p className="temperature">
            <WeatherTemperature celsius={props.data.temperature} />
            {/* 6°C */}
          </p>
        </div>
        <div className="col-sm-5">
          <p className="currentTime">
            <CurrentTime date={props.data.date} />
          </p>
          <p className="ffh">
            Feels like: {Math.round(props.data.main.feels_like)}°C°C
            {/* <span id="feelsLike">-4°C</span> */}
            <br />
            Forecast: {Math.round(props.data.main.temp_min)}°C/
            {Math.round(props.data.main.temp_max)}°C
            {/* <span id="minTemp"> 1°C</span>/<span id="maxTemp">4°C</span> */}
            <br />
            Humidity:
            {/* <span id="humidity"> 60%</span> */}
            {props.data.main.humidity} %
            <br />
            Wind:
            {/* <span id="wind">0 km/h</span> */}
            {Math.round(props.data.wind.speed)} km/h
          </p>
        </div>
      </div>
      <div className="row d-flex justify-content-start">
        <div className="description">
          {props.data.weather[0].description}
          {/* Few clouds */}
        </div>
      </div>
      <p className="owner">
        This website was coded by Olha Ovsii, and is{" "}
        <a href="https://github.com/Helga-ov/Weather" class="link">
          open-sourced
        </a>
      </p>
    </div>
  );
}
