import React, { useState } from "react";
import axios from "axios";
import WeatherCharacteristics from "./WeatherCharacteristics";
import WeatherIcon from "./WeatherIcon";
import WeatherTemperature from "./WeatherTemperature";

export default function Weather(props) {
  const [weatherData, setWeatherData] = useState({ ready: false });
  const [city, setCity] = useState(props.defaultCity);

  function handleResponse(response) {
    setWeatherData({
      ready: true,
      coordinates: response.data.coord,
      temperature: response.data.main.temp,
      humidity: response.data.main.humidity,
      date: new Date(response.data.dt * 1000),
      description: response.data.weather[0].description,
      icon: response.data.weather[0].icon,
      wind: response.data.wind.speed,
      city: response.data.name,
      min: Math.round(response.data.main.temp_min),
      max: Math.round(response.data.main.temp_max),
      feelsLike: Math.round(response.data.main.feels_like),
    });
  }

  function handleSubmit(event) {
    event.preventDefault();
    search();
  }

  function handleCityChange(event) {
    setCity(event.target.value);
  }

  function search() {
    const apiKey = "e84292aa654440af502o3b0334ef09t0";
    let apiUrl = `https://api.shecodes.io/weather/v1/current?query=${city}&key=${apiKey}`;
    axios.get(apiUrl).then(handleResponse);
  }

  if (weatherData.ready) {
    return (
      <div className="WeatherApp">
        <div className="fild">
          <div className="WeatherFild">
            <div className="container">
              <div className="row d-flex justify-content-center">
                <form onSubmit={handleSubmit}>
                  <input
                    type="text"
                    className="city2"
                    id="city1"
                    required
                    placeholder="Enter your city"
                    onChange={handleCityChange}
                  />
                  <button type="submit" className="search">
                    Search
                  </button>
                </form>
              </div>
              <div className="row d-flex justify-content-between mt-4">
                <div className="col-sm-7">
                  <button className="current">Current weather</button>
                </div>
                <div className="col-sm-5 justify-content-left">
                  <p className="lc">
                    <WeatherCharacteristics data={weatherData} />
                    {/* Vienna */}
                  </p>
                </div>
              </div>
              <div className="row d-flex justify-content-between">
                <div className="col-sm-7">
                  <WeatherIcon code={props.data.icon} className="icon" />
                  {/* <img
                    src="https://em-content.zobj.net/thumbs/120/google/350/sun-behind-cloud_26c5.png"
                    alt=""
                    className="icon"
                    id="icon1"
                  /> */}
                  <p className="temperature">
                    <WeatherTemperature celsius={props.data.temperature} />
                    {/* 6°C */}
                  </p>
                </div>
                <div className="col-sm-5">
                  <p className="currentTime">
                    <WeatherCharacteristics />
                  </p>
                  <p className="ffh">
                    Feels like: {feelsLike}°C
                    {/* <span id="feelsLike">-4°C</span> */}
                    <br />
                    Forecast: {min}°C/{max}°C
                    {/* <span id="minTemp"> 1°C</span>/<span id="maxTemp">4°C</span> */}
                    <br />
                    Humidity:
                    {/* <span id="humidity"> 60%</span> */}
                    {humidity} %
                    <br />
                    Wind:
                    {/* <span id="wind">0 km/h</span> */}
                    {wind} km/h
                  </p>
                </div>
              </div>
              <div className="row d-flex justify-content-start">
                <div className="description">
                  {description}
                  {/* Few clouds */}
                </div>
              </div>
            </div>
          </div>
          <p className="owner">
            This website was coded by Olha Ovsii, and is{" "}
            <a href="https://github.com/Helga-ov/Weather" class="link">
              open-sourced
            </a>
          </p>
        </div>
      </div>
    );
  } else {
    search();
    return "Loading...";
  }
}
